import { Component , OnInit } from '@angular/core';
import { LoggedInUserDataService } from 'src/app/services/logged-in-user-data.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  notpage:boolean=false;
  userData:any=null;
  constructor(private loggedUser:LoggedInUserDataService){
    if(sessionStorage.getItem('loggedIn'))
    {
      const storedData = sessionStorage.getItem('userData');
      if (storedData){
        this.userData = JSON.parse(storedData);
      }
      console.log("userData is in session Storeage :",this.userData.name , this.userData.id);
    }
  }
  ngOnInit() {
   }
   displayInfo(){
    const mobileIcon = document.querySelector('.mobile_icon') as HTMLElement;
    const burgerBarsIcon = document.querySelector('#burger_bars_icon') as HTMLElement;
    const navLinks = document.querySelector('.nav_links') as HTMLElement;
    const nav = document.querySelector('.mynav') as HTMLElement;
    let changer = 0;
    burgerBarsIcon.addEventListener('click', () => {
      if (changer === 0) {
         burgerBarsIcon.style.cssText = 'color:white;';
        nav.style.cssText='height:400px;';

        changer = 1;
      } else {
        navLinks.style.cssText = "height:0";
        burgerBarsIcon.style.cssText = 'color:black';
        changer = 0;
      }
     });
     if(this.userData!=null){
      const exitIcon = document.getElementById('exit_icon') as HTMLElement;
       const profileIcon = document.querySelector('.personal_profile') as HTMLElement;
       const profileDetails = document.querySelector('.profile_details') as HTMLElement;
       profileIcon.addEventListener('click', () => {
        profileDetails.style.cssText = 'right:0';
       });

       exitIcon.addEventListener('click', () => {
        profileDetails.style.cssText = 'right:-300px';
       });
     }
   }

}
      <nav class="mynav py-0 mx-0">
            <section class="container px-0">
            <a href=''class="logo">
              <!-- <img src="../../../assets/images/logo_icon.svg" alt=""> -->
                <h4>CoursePilot</h4>
            </a>
            <ul class="nav_links">
                <li><a href="">home</a></li>
                <li><a href="#">about</a></li>
                <li><a href="">contact us</a></li>
                <li><a href="/courses">Courses</a></li>
            </ul>
            <!-- <app-search></app-search> -->
            <section  (click)="displayInfo()" class="mobile_icon">
                <i class="fa-solid fa-bars " id="burger_bars_icon"></i>
            </section>
            <section (click)="displayInfo()"  *ngIf="userData!=null" class="profile_details">
              <i class="fa-solid fa-xmark" id="exit_icon"></i>
              <section class="personal_img">
              </section>
              <app-user-profile (click)="displayInfo()"   [pageType]="notpage"></app-user-profile>
            </section>
            <section (click)="displayInfo()"   *ngIf="userData!=null" class="personal_profile">
                <section class="personal_img">
                </section>
                <section class="personal_info">
                    <p class="personal_name"> {{userData.name}} </p>
                    <span *ngIf="!userData.type" class="personal_case">Student</span>
                    <span *ngIf="userData.type" class="personal_case">Instructor</span>
                </section>
            </section>
            <section>
              <div *ngIf="userData==null" class="border border-1 border-white">
                <a  class="bj-button bj-button--sm bj-button--outline bj-button--inverted track" routerLink="login">Log in</a>               </div>
            </section>
        </section>
    </nav>
