import { Component, OnInit, Type, ViewChild, ViewContainerRef, ComponentRef, ComponentFactoryResolver, OnDestroy } from '@angular/core';
import { MyDataService } from '../../../services/my-data.service';
import { VideoComponent } from '../../video/video.component';
import { PdfComponent } from '../../pdf/pdf.component';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';


@Component({
  selector: 'app-course-details',
  templateUrl: './course-details.component.html',
  styleUrls: ['./course-details.component.scss'],
})
export class CourseDetailsComponent implements OnInit {
  userData;
  courseId:any=4;
  courseDetails: any;
  courseReviews: any;
  commentForm: FormGroup;
  vidID = 'MbTM6xnl5Qc';
  private componentRef?: ComponentRef<any>;
  changeID(newID: string) {
    this.vidID = newID;
  }
  content: any = [
    { week: 1, pdf: 'PathPDF', video: 'Chapter1Vid1' },
    { week: 2, pdf: 'Chapter2', video: 'Chapter2Vid1' },
    { week: 2, pdf: 'Chapter2', video: 'Chapter2Vid2' },
    { week: 3, pdf: 'Chapter3', video: 'Chapter3Vid' },
    { week: 4, pdf: 'Chaptet4', video: 'Chapter4Vid' },
    { week: 5, pdf: 'Chapter5', video: 'Chapter5Vid1' },
    { week: 5, pdf: 'Chapter5', video: 'Chapter5Vid2' },
  ];

  constructor(private formBuilder: FormBuilder ,private MyDataService: MyDataService, private componentFactoryResolver: ComponentFactoryResolver, private route:ActivatedRoute , private router:Router) {
    if(!sessionStorage.getItem('loggedIn')){
      alert("please Login to see Coures Details")
       this.router.navigate(['login']);
    }
    this.userData=sessionStorage.getItem('userData');
    this.userData= JSON.parse(this.userData);
    console.log(this.userData.id);
   }
  @ViewChild('container', { read: ViewContainerRef, static: true })
  container!: ViewContainerRef;
  createComponent(newid: string) {
    this.container.clear();
    const cType=this.getComponentType(newid);
    const component = this.container.createComponent(cType);
    component.instance.id = newid;
    // console.log(newid);
   }
   getComponentType(name:string): Type<any>{
    let type : Type<any> = VideoComponent;
    if(name.slice(-4) === ".pdf")
    {
      type = PdfComponent;
      return type;
    }
    return type
   }

  ngOnInit(): void {
    this.commentForm = this.formBuilder.group({
      comment: ['', Validators.required],
      rating: [1, Validators.required],
      user_id: this.userData.id,
      course_id:this.courseId
    });

    this.createComponent('gnTFkl2AF-w');
    this.route.paramMap.subscribe((params) => {
      this.courseId = params.get('id');
      this.commentForm.value.course_id = this.courseId;
      this.fetchCourseDetails();
    });

    this.fetchCourseDetails();

    console.log("course id is " , this.courseId);

    this.MyDataService.AllComments(this.courseId).subscribe((data) => {
      console.log(this.courseId)
      this.courseReviews = data;
      console.log('course reviews : ' ,this.courseReviews );
    });
  }


  fetchCourseDetails(): void {
    this.MyDataService.getCourse(this.courseId).subscribe((data) => {
      this.courseDetails = data;
      console.log(this.courseDetails)
    });
  }
  // ngOnDestroy(): void {
  //    if (this.componentRef) {
  //     this.componentRef.destroy();
  //   }
  // }

  get contentByWeek() {
    const contentByWeek: {
      week: any;
      items: any[] } [] = [];

    // Group content by week
    this.content.forEach((item: { week: any }) => {
      const weekIndex = contentByWeek.findIndex(
        (week) => week.week === item.week
      );
      if (weekIndex === -1) {
        contentByWeek.push({ week: item.week, items: [item] });
      } else {
        contentByWeek[weekIndex].items.push(item);
      }
    });
    return contentByWeek;
  }

  // submitComment(){
  //      console.log(this.commentForm.value)
  //     this.MyDataService.comment(this.form.value).subscribe(
  //     (response:any) => {
  //       alert("course Created Successfully! ")
  //       this.router.navigate(['userProfile']);


  //      },
  //     (error) => {
  //        console.error('Error:', error);
  //          alert(error.messeage);
  //       }
  //     );
  //  }
}
<main>
  <header>
    <div class="row d-flex justify-content-between border-right bg-gradiantt">
      <div class="logo p-2 py-3 "><a class="badge-dark" href="home_page.html">CoursePilot</a></div>

      <div class="d-flex justify-content-between align-items-center mx-2 text-center  p-2 py-3 contact_bt">
              <a routerLink="/courses" >
                <i class="fa-sharp fa-solid fa-arrow-left fa-bounce" style="color: #ffffff;"></i>
                Back</a>
      </div>

     </div>
  </header>
  <section class="d-flex flex-row gap-2 justify-content-between" style="height: 100%;">
    <aside class=" col-4 d-flex flex-column bg py-2 overflow-auto pos-fixed fixed" style="height: 90vh;">
      <div class="card-body py-0" *ngFor="let week of contentByWeek">
        <div class="flex-row" >
          <div class="col-sm-12">
            <p class="mb-0 text-sm-center"> <a class="hiphop_text"> Week : {{ week.week }}</a></p>
          </div>
          <div *ngFor="let item of week.items">
            <div class="col-sm-12 d-flex flex-row justify-content-between" >
              <p><i class="fa-regular fa-file-pdf"></i> <a class="mx-1" (click)="createComponent('Ahmed.pdf')">{{ item.pdf }}</a></p>
              <a  style="text-decoration: underline !important; color: blue;  height:20px; align-self: center;" href="#">Summary</a>
            </div>
            <div class="col-sm-12 d-flex flex-row justify-content-between" >
              <p> <i class="fa-solid fa-video"></i> <a class="mx-1"  (click)="createComponent('kTgyVW3WIAs')">{{ item.video }} </a></p>
              <a   style="text-decoration: underline !important; color: blue; height:20px; align-self: center;"  href="#">Summary</a>
            </div>
           </div>
        </div>
        <hr>
      </div>
    </aside>
    <div class="align-self-center my-2 w-100">
      <ng-container class="col-8" #container>
      </ng-container>
    </div>
   </section>
   <hr class="m-0 p-0">

   <section class="d-flex flex-row gap-2 " >
    <div class=" col-4  my-2 align-self-center" style="height: 100%;">
      <div class="card bg">
        <div class="card-body">
          <h5 class="card-title text-center"> {{courseDetails[0].name}} </h5>
          <h6 class="card-subtitle mb-2 text-muted">Category : {{courseDetails[0].category_name}}</h6>
          <p class="card-text">{{courseDetails[0].description}}</p>
          <div class="d-flex justify-content-between">
            <a href="#" class="card-link">{{courseDetails[0].instructor_name}}</a>
            <a href="#" class="card-link">num of students : {{courseDetails[0].user_count}}</a>
          </div>
        </div>
      </div>
    </div>
    <div class="reviews col-8 overflow-auto" style="height:100%; padding-right: 30px;">
      <h2 class="text-center">Course Reviews </h2>
         <table class="table bg" >
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">User Name</th>
              <th scope="col">Comment</th>
              <th scope="col">Rate</th>
            </tr>
          </thead>
          <tbody>
            <tr  *ngFor="let review of courseReviews; let i = index" >
              <th scope="row">{{i+1}}</th>
