import { Component ,Input,OnInit} from '@angular/core';
import { LoggedInUserDataService } from 'src/app/services/logged-in-user-data.service';
import { Router } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { MyDataService } from 'src/app/services/my-data.service';



@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.scss']
})
export class UserProfileComponent implements OnInit {
  page:boolean=true;
  userData:any;
  userComments:any;
  numOfCourses:number=0;
  isInputDisabled: boolean = true;

  @Input() pageType:boolean=true;

  constructor( private myApi:MyDataService, private loggedUser:LoggedInUserDataService , private user:LoggedInUserDataService , private router:Router){
    this.pageType=this.page;
    if(sessionStorage.getItem('loggedIn'))
    {
      const storedData = sessionStorage.getItem('userData');
      if (storedData){
        this.userData = JSON.parse(storedData);
      }
     }
    else{
      alert("You are not logged in. Please log in to continue.");
      this.router.navigate(['login']);
    }
  }

  private EditData() {

    this.loggedUser.updateUser(this.userData.id, this.userData).subscribe(
      (response) => {
        alert('User data updated');
        this.loggedUser.updateUserData(this.userData);
        this.isInputDisabled=true;
      },
      (error) => {
        alert('Error');
        // Handle error or display error message
      }
    );
  }
  private GetComments(){
    this.user.userComments(this.userData.id).subscribe(
      (response) => {
        // console.log(response.length);
        this.user = response;
        this.userComments=response;
        },
      (error) => {
        alert('Error');
        // Handle error or display error message
      }
    );
  }
  editProfile(): void {
    this.isInputDisabled=false;
   }
  //  getUserComments(){
  //   return this.userComments.length;
  // }
  logout(){
    sessionStorage.removeItem('userData');
    sessionStorage.removeItem('loggedIn');
    location.reload();
    console.log("Logged Out");
  }
  ngOnInit(): void {
    this.getUserCourses();
  }
  getUserCourses(): void {
    this.myApi.userCourses(this.userData.id).subscribe(
      (response) => {
        this.numOfCourses = response.length;
        // console.log(response)
        },
      (error) => {
        console.error('errror is ' , error)
        // console.log('param is ' , this.userData.id);
      }
    );
}
Update(): void {
  this.EditData();
}
Comments():void{
  this.GetComments();
}
}
<section *ngIf="pageType">
  <div
    class="col-md-12 d-flex justify-content-between border-right bg-gradiantt"
  >
    <div class="logo p-2 py-3">
      <a class="badge-dark" routerLink="">Course Pilot</a>
    </div>

    <div
      class="d-flex justify-content-between align-items-center mx-2 text-center p-2 py-3 contact_bt"
    >
      <a  routerLink="">
        <i
          class="fa-sharp fa-solid fa-arrow-left fa-bounce"
          style="color: #ffffff"
        ></i>
        Back</a
      >
    </div>
  </div>
  <div class="row contact1 mx-0 py-4">
    <div class="col-lg-4 px-0">
      <div class="bg card mb-4 mx-2">
        <div class="card-body text-center">
          <img
            src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava6.webp"
            alt="avatar"
            class="rounded-circle img-fluid"
            style="width: 150px"
          />
          <p class="text-muted mb-1" *ngIf="userData.type">Instructor</p>
          <p class="text-muted mb-1" *ngIf="!userData.type">Student</p>
          <div class="d-flex flex-column">
            <input  class="bg-transparent text-center" [(ngModel)]="userData.name" [disabled]="isInputDisabled" />
            <input class="bg-transparent text-center"  type="text" [(ngModel)]="userData.email" [disabled]="isInputDisabled" />
            <!-- <input class="bg-transparent text-center"  type="password" [(ngModel)]="userData.password" [disabled]="isInputDisabled" /> -->
          </div>
          <a *ngIf="isInputDisabled" class="badge" style="background: floralwhite; color: gray" href="#" (click)="editProfile()">Edit</a>
          <a *ngIf="!isInputDisabled" class="badge btn-danger" style="background: rgb(81, 57, 7); color: white" href="#" (click)="this.Update()">Save</a>
         </div>
      </div>
    </div>
    <div class="col-lg-8 px-0">
      <div class="bg card mb-4">
        <div class="card-body">
          <div class="d-flex flex-column">
            <div class="row">
            <div class="col">
              <p class="mb-0">My Courses</p>
            </div>
            <div *ngIf="userData.type==1" class="col-sm-4">
              <a  [routerLink]="['/newCourse']" class="badge" style="background: floralwhite; color: gray; margin-top: 19px;">new Course</a>
            </div>
          </div>
              <div class="col-sm-8">
                <p><a [routerLink]="['/userCourses']">{{numOfCourses}}</a></p>
              </div>
          </div>
          <hr />
          <div *ngIf="!userData.type" class="flex-row">
            <div class="col-sm-3">
