import { Component ,Input,OnInit , Output ,EventEmitter } from '@angular/core';

@Component({
  selector: 'app-trending-now',
  templateUrl: './trending-now.component.html',
  styleUrls: ['./trending-now.component.scss']
})
export class TrendingNowComponent implements OnInit {
@Input() catNames : string[] = ["Development" ,"Designing" , "Business" , "Social" ];
@Output() onTabChange =new EventEmitter<number>();
 activTab:any;
  constructor(){

  }
  ngOnInit(): void {
   }
  setActive(i:number){
    this.activTab =i;
    this.onTabChange.emit(this.activTab);
    // console.log(this.catArray[this.activTab]);
  }

}
    <div class="row">
      <div class="col-md-12">
        <ul class="nav nav-tabs bg-gray-200">
          <li class="nav-item" (click)="setActive(-1)">
            <a class="nav-link" [ngClass]="activTab == -1 ? 'activated' : ''">
              All
            </a>
          </li>
          <li
            class="nav-item"
            *ngFor="let cat of catNames; let in = index"
            (click)="setActive(in)"
          >
            <a class="nav-link" [ngClass]="activTab == in ? 'activated' : ''">{{ cat }}</a>
          </li>
        </ul>
      </div>
    </div>
