import { MyDataService } from '../../../services/my-data.service';
import { LoggedInUserDataService } from '../../../services/logged-in-user-data.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable, count, map } from 'rxjs';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.scss']
})

export class HomePageComponent implements OnInit {
   user:any=sessionStorage.getItem('userData');
   categoriesNames:any=["Development" ,"Designing" , "Business" , "Social" , "Cokking" ];
   categoriesData:any=[];
   activatedTab:any;
   noActiveTab:boolean= true;
   apiAllCourses : any;
  constructor( private myApi:MyDataService , private route: ActivatedRoute , private loggedUser:LoggedInUserDataService){
  }

  ngOnInit(): void {
    // console.log(this.user);
    this.myApi.getAllCategories().subscribe((data)=>{
      this.myApi=data;
      this.categoriesData=data;
      this.categoriesNames = data.map((item: { name: any; }) => item.name);
      // console.log(this.categoriesArray);
    })

    this.myApi.AllCourses().subscribe((courses)=>{
       this.apiAllCourses = courses;
       this.apiAllCourses.sort((a:any, b:any) => {
        return b.category_id - a.category_id;
      });
      //  console.log(this.apiAllCourses);
    })

   }

  getCategoryNameById(id: number): string {
    const category = this.categoriesData.find((item:any) => item.id === id);
    return category ? category.name : '';
  }

   tabChange(i:number){
    this.noActiveTab=false;
    this.activatedTab=i;
  }


}
<app-header></app-header>
<app-landing></app-landing>
<section class="py-5 bg-white">
  <div class="container">
  <div class="row row-cols-2 justify-content-between mb-3">
    <h2 class="h1 fs-4 m-0">Trending Courses</h2>
    <div class="button m-0">
      <div class="button-layer"></div>
      <a routerLink="allCourses">All Courses</a>
    </div>
  </div>
<app-trending-now [catNames]="categoriesNames" (onTabChange)="tabChange($event)"></app-trending-now>
</div>
</section>
<section class="container-md " >
  <div class="row">
    <ng-container *ngFor="let course of apiAllCourses">
        <app-course-card  class="col-xl-4 col-md-6 col-sm-6  my-2" *ngIf=" getCategoryNameById(course.category_id) ==  categoriesNames[activatedTab] || noActiveTab || activatedTab==-1 " [id]="course.id" [title]="course.name" [description]="course.description" [image]="course.cover" [category]="getCategoryNameById(course.category_id)" [numOfStudents]="(course.user_count)" [instructor]="course.instructor_name"></app-course-card>
    </ng-container>
  </div>
</section>
